mapper.select:
SELECT uf.alarm_id, ufu.action_start_time, uf.line_id, uf.process_id, uf.alarm_type, aad.action_detail, ufu.completion_status, ufu.action_completion_time FROM unexpected_faults uf JOIN unexpected_faults_user_input ufu ON uf.alarm_id = ufu.alarm_id LEFT JOIN ( SELECT alarm_id, action_detail FROM alert_action_details aad1 WHERE action_sequence = ( SELECT MAX(action_sequence) FROM alert_action_details aad2 WHERE aad1.alarm_id = aad2.alarm_id ) ) aad ON uf.alarm_id = aad.alarm_id WHERE 1=1
AND uf.line_id = #{lineId}
AND uf.process_id = #{processId}
ORDER BY ufu.action_start_time DESC
