mapper.update:
UPDATE user as u
SET u.factory_id = #{request.factoryId}, u.dept_id = #{request.departmentId}, u.position = #{request.position}, u.role = #{request.role}
WHERE u.user_id = #{userId};

mapper.select:
SELECT u.*
FROM USER as u
WHERE u.user_id = #{userId}
LIMIT 1;

SELECT u.*
FROM USER as u
WHERE u.employ_id = #{employeeId}
LIMIT 1;

SELECT u.user_id, f.factory_name, u.name, u.employ_id, d.dept_name, u.position, u.role FROM USER AS u LEFT JOIN FACTORY AS f ON f.factory_id = u.factory_id LEFT JOIN DEPARTMENT AS d ON d.dept_id = u.dept_id
WHERE f.factory_name LIKE CONCAT('%', #{keyword}, '%') OR u.name LIKE CONCAT('%', #{keyword}, '%') OR u.employ_id LIKE CONCAT('%', #{keyword}, '%') OR d.dept_name LIKE CONCAT('%', #{keyword}, '%') OR u.position LIKE CONCAT('%', #{keyword}, '%') OR u.role LIKE CONCAT('%', #{keyword}, '%')
WHERE f.factory_name LIKE CONCAT('%', #{keyword}, '%') u.name LIKE CONCAT('%', #{keyword}, '%') u.employ_id LIKE CONCAT('%', #{keyword}, '%') d.dept_name LIKE CONCAT('%', #{keyword}, '%') u.position LIKE CONCAT('%', #{keyword}, '%') u.role LIKE CONCAT('%', #{keyword}, '%')
ORDER BY u.user_id DESC
LIMIT #{size}
OFFSET #{offset}
